"use client";
import { motion } from "framer-motion";

export default function DateTimePicker({ dateTime, setDateTime, onNext, onBack }) {
    const today = new Date().toISOString().split("T")[0]; // disable past dates
    const times = [
        "09:00 AM", "10:00 AM", "11:00 AM",
        "12:00 PM", "01:00 PM", "02:00 PM",
        "03:00 PM", "04:00 PM", "05:00 PM"
    ];

    const isValid = dateTime.date && dateTime.time;

    return (
        <motion.div
            key="date-time-step"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -50 }}
            transition={{ duration: 0.4 }}
            className="space-y-4"
        >
            <div>
                <label className="block text-sm mb-1">Select Date</label>
                <input
                    type="date"
                    min={today}
                    value={dateTime.date}
                    onChange={(e) => setDateTime((prev) => ({ ...prev, date: e.target.value }))}
                    className="w-full px-4 py-3 rounded-lg bg-black/30 border border-gray-600 focus:border-orange-500 focus:ring-2 focus:ring-orange-500 outline-none"
                    required
                />
            </div>

            <div>
                <label className="block text-sm mb-1">Select Time</label>
                <select
                    value={dateTime.time}
                    onChange={(e) => setDateTime((prev) => ({ ...prev, time: e.target.value }))}
                    className="w-full px-4 py-3 rounded-lg bg-black/30 border border-gray-600 focus:border-orange-500 focus:ring-2 focus:ring-orange-500 outline-none"
                    required
                >
                    <option value="">-- Select Time --</option>
                    {times.map((t) => (
                        <option key={t} value={t}>{t}</option>
                    ))}
                </select>
            </div>

            <div className="flex justify-between">
                <button
                    type="button"
                    onClick={onBack}
                    className="py-3 px-6 rounded-lg bg-gray-700 hover:bg-gray-600 font-semibold transition"
                >
                    Back
                </button>

                <button
                    type="button"
                    disabled={!isValid}
                    onClick={() => onNext(dateTime)}
                    className={`py-3 px-6 rounded-lg font-semibold transition ${
                        isValid
                            ? "bg-orange-500 hover:bg-orange-600"
                            : "bg-gray-600 cursor-not-allowed"
                    }`}
                >
                    Next
                </button>
            </div>
        </motion.div>
    );
}
