+  { key: 'other', label: 'Other' },
+];
+
+export default function ExpensesCard() {
+  const [items, setItems] = useState([]);
+  const [loading, setLoading] = useState(true);
+  const [error, setError] = useState('');
+
+  async function load() {
+    setLoading(true);
