import { useEffect, useMemo, useState } from 'react';
import { Bar } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, BarElement, Tooltip, Legend } from 'chart.js';
Chart.register(CategoryScale, LinearScale, BarElement, Tooltip, Legend);

const CATEGORIES = [
  { key: 'one-time', label: 'Equipment' }, // one-time purchases like vacuum
  { key: 'chemicals', label: 'Chemicals' },
  { key: 'other', label: 'Other' },
];

export default function ExpensesCard() {
  const [items, setItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [form, setForm] = useState({ date: '', amount: '', category: 'one-time', note: '' });
  const [submitting, setSubmitting] = useState(false);

  async function load() {
    setLoading(true);
    try {
      const res = await fetch('/api/expenses');
      const data = await res.json();
      if (res.ok && data.success) setItems(data.items || []);
      else setError(data.error || 'Failed to load expenses');
    } catch (e) {
      setError(e.message || 'Failed to load expenses');
    } finally { setLoading(false); }
  }

  useEffect(() => { load(); }, []);

  async function addExpense(e) {
    e.preventDefault();
    setSubmitting(true);
    setError('');
    try {
      const body = { ...form, amount: Number(form.amount) };
      const res = await fetch('/api/expenses', { method: 'POST', headers: { 'Content-Type': 'application/json' }, body: JSON.stringify(body) });
      const data = await res.json();
      if (res.ok && data.success) {
        setItems(prev => [data.item, ...prev]);
        setForm({ date: '', amount: '', category: form.category, note: '' });
      } else setError(data.error || 'Failed to add');
    } catch (e) { setError(e.message || 'Failed to add'); }
    setSubmitting(false);
  }

  const monthly = useMemo(() => {
    const map = {};
    for (const it of items) {
      const key = (it.date || '').slice(0,7); // YYYY-MM
      const cat = it.category || 'other';
      if (!map[key]) map[key] = { total: 0, byCat: { 'one-time': 0, 'chemicals': 0, 'other': 0 } };
      map[key].total += Number(it.amount || 0);
      map[key].byCat[cat] += Number(it.amount || 0);
    }
    const keys = Object.keys(map).sort();
    return { keys, map };
  }, [items]);

  const chartData = useMemo(() => {
    const labels = monthly.keys;
    const data = {
      labels,
      datasets: [
        { label: 'Total', data: labels.map(k => monthly.map[k].total), backgroundColor: 'rgba(239,68,68,0.6)' },
      ],
    };
    return data;
  }, [monthly]);

  const chartOptions = { responsive: true, maintainAspectRatio: false, plugins: { legend: { display: true } } };

  return (
    <div className="bg-white rounded-xl shadow p-6 border border-blue-100">
      <div className="flex items-center justify-between mb-4">
        <div className="text-lg font-semibold text-blue-700">Expenses</div>
      </div>
      {/* Add form */}
      <form onSubmit={addExpense} className="grid grid-cols-1 md:grid-cols-5 gap-3 items-end">
        <div>
          <label className="text-xs text-gray-600">Date</label>
          <input type="date" value={form.date} onChange={e => setForm(f => ({ ...f, date: e.target.value }))} className="w-full px-3 py-2 rounded border border-blue-200" required />
        </div>
        <div>
          <label className="text-xs text-gray-600">Amount</label>
          <input type="number" min="0" step="1" value={form.amount} onChange={e => setForm(f => ({ ...f, amount: e.target.value }))} className="w-full px-3 py-2 rounded border border-blue-200" required />
        </div>
        <div>
          <label className="text-xs text-gray-600">Category</label>
          <select value={form.category} onChange={e => setForm(f => ({ ...f, category: e.target.value }))} className="w-full px-3 py-2 rounded border border-blue-200">
            {CATEGORIES.map(c => <option key={c.key} value={c.key}>{c.label}</option>)}
          </select>
        </div>
        <div className="md:col-span-2">
          <label className="text-xs text-gray-600">Note</label>
          <input type="text" value={form.note} onChange={e => setForm(f => ({ ...f, note: e.target.value }))} placeholder="Optional" className="w-full px-3 py-2 rounded border border-blue-200" />
        </div>
        <div className="md:col-span-5">
          <button type="submit" disabled={submitting} className="px-4 py-2 rounded bg-blue-600 text-white font-semibold">{submitting ? 'Adding...' : 'Add Expense'}</button>
        </div>
      </form>
      {error && <div className="text-red-600 text-sm mt-2">{error}</div>}
      {/* Chart */}
      <div className="w-full mt-4 bg-white rounded-lg p-2" style={{ height: '240px' }}>
        <Bar data={chartData} options={chartOptions} />
      </div>
    </div>
  );
}
