+"use client";
+import { useEffect, useState } from "react";
+import { Swiper, SwiperSlide } from "swiper/react";
+import { Autoplay } from "swiper";
+import "swiper/css";
+
+const galleryImages = [
+  "/images/slides/slide1.jpg",
+  "/images/slides/slide2.jpg",
+  "/images/slides/slide3.jpg",
+  "/images/slides/slide4.jpg",
+];
+
+export default function Gallery() {
+  const [isClient, setIsClient] = useState(false);
+
+  useEffect(() => {
+    setIsClient(true);
+  }, []);
+
+  if (!isClient) return null; // prevent SSR issues
+
+  return (
+    <section id="gallery" className="py-20 bg-gray-900">
+      <div className="max-w-7xl mx-auto px-6 text-center">
+        <h2 className="text-4xl font-extrabold text-orange-400 mb-12">
+          Gallery
+        </h2>
+
+        <Swiper
+          modules={[Autoplay]}
+          spaceBetween={20}
+          slidesPerView={1}
+          loop={true}
+          speed={4000} // speed of slide movement (higher = smoother/slower)
+          autoplay={{
+            delay: 0, // no pause
+            disableOnInteraction: false,
+          }}
+          freeMode={true} // continuous, no snapping
+          freeModeMomentum={false}
+          breakpoints={{
+            640: { slidesPerView: 2 },
+            1024: { slidesPerView: 3 },
+          }}
+        >
+          {galleryImages.map((img, index) => (
+            <SwiperSlide key={index}>
+              <div className="overflow-hidden rounded-2xl cursor-pointer hover:scale-105 transition-transform duration-500">
+                <img
+                  src={img}
+                  alt={`Gallery ${index + 1}`}
+                  className="w-full h-64 object-cover"
+                />
+              </div>
+            </SwiperSlide>
+          ))}
+        </Swiper>
+      </div>
+    </section>
+  );
+}

