@@ -0,0 +1,149 @@
+"use client";
+import { motion } from "framer-motion";
+
+export default function ReviewInfo({
+    service,
+    vehicle,
+    userInfo,
+    dateTime,
+    onBack,
+    onSubmit,
+}) {
+    // Handle price formatting if it's a string with |
+    const formatPrice = (text) => {
+        // Only $ and numbers green
+        return text.split(" ").map((part, i) => {
+            if (part.startsWith("$") || /^\d+$/.test(part.replace("$", ""))) {
+                return (
+                    <span key={i} className="text-green-400 font-bold">
+                        {part}{" "}
+                    </span>
+                );
+            }
+            return <span key={i}>{part} </span>;
+        });
+    };
+
+    let priceContent;
+    if (service?.prices) {
+        // Object form
+        priceContent = (
+            <div className="flex flex-col items-end space-y-1">
+                {Object.entries(service.prices).map(([type, price], i) => (
+                    <span key={i} className="text-sm sm:text-base">
+                        <span className="text-gray-200">{type}:</span>{" "}
+                        <span className="text-green-400 font-bold">
+                            ${price}
+                        </span>
+                    </span>
+                ))}
+            </div>
+        );
+    } else if (
+        typeof service?.price === "string" &&
+        service.price.includes("|")
+    ) {
+        priceContent = (
+            <div className="flex flex-col items-end space-y-1">
+                {service.price.split("|").map((p, i) => (
+                    <span key={i} className="text-sm sm:text-base">
+                        {formatPrice(p.trim())}
+                    </span>
+                ))}
+            </div>
+        );
+    } else {
+        priceContent = (
+            <span className="text-green-400 font-bold text-lg sm:text-xl">
+                ${service.price || "TBD"}
+            </span>
+        );
+    }
+
+    return (
+        <motion.div
+            key="review-step"
+            initial={{ opacity: 0, y: 50 }}
+            animate={{ opacity: 1, y: 0 }}
+            exit={{ opacity: 0, y: 50 }}
+            transition={{ duration: 0.4 }}
+            className="flex justify-center items-start min-h-screen bg-black/30 p-4 overflow-y-auto"
+        >
+            <div className="bg-gray-900 rounded-2xl p-5 max-w-lg w-full space-y-6">
+                {/* Close Button */}
+                <div className="flex justify-end mb-2">
+                    <button className="text-orange-500 text-xl font-bold hover:text-orange-400">
+                        ✕
+                    </button>
+                </div>
+
+                <h4 className="text-lg sm:text-xl font-bold text-orange-400 mb-4 text-center">
+                    Review Your Booking
+                </h4>
+
+                {/* Review Info Box */}
+                <div className="bg-gray-800 p-4 rounded-xl shadow-md space-y-3 text-sm sm:text-base">
+                    {[
+                        { label: "Service", value: service.title },
+                        { label: "Price", value: priceContent, isPrice: true },
+                        {
+                            label: "Vehicle",
+                            value: `${vehicle.year} ${vehicle.name}`,
+                        },
+                        { label: "Date", value: dateTime?.date || "N/A" },
+                        { label: "Time", value: dateTime?.time || "N/A" },
+                        { label: "Name", value: userInfo.name },
+                        { label: "Email", value: userInfo.email },
+                        {
+                            label: "Phone",
+                            value: `${userInfo.countryCode || ""} ${
+                                userInfo.phone
+                            }`,
+                        },
+                        userInfo.message && {
+                            label: "Notes",
+                            value: userInfo.message,
+                        },
+                    ]
+                        .filter(Boolean)
+                        .map((item, idx) => (
+                            <div
+                                key={idx}
+                                className="flex justify-between items-start border-b border-gray-700 pb-1 last:border-b-0"
+                            >
+                                <span className="font-medium text-gray-300">
+                                    {item.label}
+                                </span>
+                                <span
+                                    className={`text-right ${
+                                        item.isPrice ? "" : "text-gray-100"
+                                    }`}
+                                >
+                                    {item.value}
+                                </span>
+                            </div>
+                        ))}
+                </div>
+
+                {/* Buttons */}
+                <div className="flex flex-col sm:flex-row gap-3 sm:justify-between sticky bottom-0 bg-gray-900 py-3">
+                    <button
+                        type="button"
+                        onClick={onBack}
+                        className="py-3 px-6 rounded-lg bg-gray-700 hover:bg-gray-600 font-semibold transition"
+                    >
+                        Back
+                    </button>
+
+                    <button
+                        type="submit"
+                        onClick={onSubmit}
+                        className="py-3 px-6 rounded-lg bg-orange-500 hover:bg-orange-600 font-bold transition"
+                    >
+                        Confirm Booking
+                    </button>
+                </div>
+            </div>
+        </motion.div>
+    );
+}

