"use client";
import { motion } from "framer-motion";

export default function ReviewInfo({ service, vehicle, userInfo, onBack, onSubmit }) {
    return (
        <motion.div
            key="review-step"
            initial={{ opacity: 0, x: 50 }}
            animate={{ opacity: 1, x: 0 }}
            exit={{ opacity: 0, x: -50 }}
            transition={{ duration: 0.4 }}
            className="space-y-4"
        >
            <h4 className="text-lg font-semibold text-orange-400 mb-2">
                Review Your Booking
            </h4>

            <div className="bg-gray-800 p-4 rounded-lg space-y-2">
                <p><strong>Service:</strong> {service.title}</p>
                <p><strong>Price:</strong> ${service.price || "TBD"}</p>
                <p><strong>Car:</strong> {vehicle.name} ({vehicle.year})</p>
                <p><strong>Name:</strong> {userInfo.name}</p>
                <p><strong>Email:</strong> {userInfo.email}</p>
                <p><strong>Phone:</strong> {userInfo.phone}</p>
                {userInfo.message && <p><strong>Notes:</strong> {userInfo.message}</p>}
            </div>

            <div className="flex justify-between">
                <button
                    type="button"
                    onClick={onBack}
                    className="py-3 px-6 rounded-lg bg-gray-700 hover:bg-gray-600 font-semibold transition"
                >
                    Back
                </button>

                <button
                    type="submit"
                    onClick={onSubmit}
                    className="py-3 px-6 rounded-lg bg-orange-500 hover:bg-orange-600 font-semibold transition"
                >
                    Confirm Booking
                </button>
            </div>
        </motion.div>
    );
}
