+"use client";
+import { motion } from "framer-motion";
+import { useState } from "react";
+
+export default function UserInfo({ userInfo, setUserInfo, onNext, onBack }) {
+    const [touched, setTouched] = useState({
+        name: false,
+        email: false,
+        phone: false,
+    });
+
+    const isValidEmail = /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(userInfo.email);
+    const isValidPhone = /^[0-9]{10}$/.test(userInfo.phone); // 10-digit phone
+    const isValidName = userInfo.name.trim().length > 1;
+
+    const isFormValid = isValidEmail && isValidPhone && isValidName;
+
+    return (
+        <motion.div
+            key="user-step"
+            initial={{ opacity: 0, x: 50 }}
+            animate={{ opacity: 1, x: 0 }}
+            exit={{ opacity: 0, x: -50 }}
+            transition={{ duration: 0.4 }}
+            className="space-y-4"
+        >
+            {/* Name */}
+            <div>
+                <label className="block text-sm mb-1">Name</label>
+                <input
+                    type="text"
+                    placeholder="Your Name"
+                    value={userInfo.name}
+                    onChange={(e) =>
+                        setUserInfo((prev) => ({ ...prev, name: e.target.value }))
+                    }
+                    onBlur={() => setTouched((prev) => ({ ...prev, name: true }))}
+                    className={`w-full px-4 py-3 rounded-lg bg-black/30 border 
+                        ${!isValidName && touched.name ? "border-red-500 focus:border-red-500 focus:ring-red-500" : "border-gray-600 focus:border-orange-500 focus:ring-orange-500"}
+                        focus:ring-2 outline-none`}
+                    required
+                />
+                {!isValidName && touched.name && (
+                    <p className="text-red-500 text-sm mt-1">Enter a valid name</p>
+                )}
+            </div>
+
+            {/* Email */}
+            <div>
+                <label className="block text-sm mb-1">Email</label>
+                <input
+                    type="email"
+                    placeholder="Your Email"
+                    value={userInfo.email}
+                    onChange={(e) =>
+                        setUserInfo((prev) => ({ ...prev, email: e.target.value }))
+                    }
+                    onBlur={() => setTouched((prev) => ({ ...prev, email: true }))}
+                    className={`w-full px-4 py-3 rounded-lg bg-black/30 border 
+                        ${!isValidEmail && touched.email ? "border-red-500 focus:border-red-500 focus:ring-red-500" : "border-gray-600 focus:border-orange-500 focus:ring-orange-500"}
+                        focus:ring-2 outline-none`}
+                    required
+                />
+                {!isValidEmail && touched.email && (
+                    <p className="text-red-500 text-sm mt-1">Enter a valid email</p>
+                )}
+            </div>
+
+            {/* Phone */}
+            <div>
+                <label className="block text-sm mb-1">Phone</label>
+                <input
+                    type="tel"
+                    placeholder="Your Phone Number"
+                    value={userInfo.phone}
+                    onChange={(e) =>
+                        setUserInfo((prev) => ({ ...prev, phone: e.target.value }))
+                    }
+                    onBlur={() => setTouched((prev) => ({ ...prev, phone: true }))}
+                    className={`w-full px-4 py-3 rounded-lg bg-black/30 border 
+                        ${!isValidPhone && touched.phone ? "border-red-500 focus:border-red-500 focus:ring-red-500" : "border-gray-600 focus:border-orange-500 focus:ring-orange-500"}
+                        focus:ring-2 outline-none`}
+                    required
+                />
+                {!isValidPhone && touched.phone && (
+                    <p className="text-red-500 text-sm mt-1">Enter a valid 10-digit phone number</p>
+                )}
+            </div>
+
+            {/* Notes */}
+            <div>
+                <label className="block text-sm mb-1">Additional Notes</label>
+                <textarea
+                    rows="3"
+                    placeholder="Any extra info..."
+                    value={userInfo.message}
+                    onChange={(e) =>
+                        setUserInfo((prev) => ({ ...prev, message: e.target.value }))
+                    }
+                    className="w-full px-4 py-3 rounded-lg bg-black/30 border border-gray-600 
+                    focus:border-orange-500 focus:ring-2 focus:ring-orange-500 outline-none"
+                ></textarea>
+            </div>
+
+            {/* Buttons */}
+            <div className="flex justify-between">
+                <button
+                    type="button"
+                    onClick={onBack}
+                    className="py-3 px-6 rounded-lg bg-gray-700 hover:bg-gray-600 font-semibold transition"
+                >
+                    Back
+                </button>
+
+                <button
+                    type="button"
+                    onClick={onNext}
+                    disabled={!isFormValid}
+                    className={`py-3 px-6 rounded-lg font-semibold transition 
+                        ${isFormValid 
+                            ? "bg-orange-500 hover:bg-orange-600" 
+                            : "bg-gray-600 cursor-not-allowed"
+                        }`}
+                >
+                    Review Booking
+                </button>
+            </div>
+        </motion.div>
+    );
+}

