import { useEffect, useState } from 'react';
import { Line } from 'react-chartjs-2';
import { Chart, CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend } from 'chart.js';
Chart.register(CategoryScale, LinearScale, PointElement, LineElement, Tooltip, Legend);

export default function VisitorsCard() {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [total, setTotal] = useState(0);
  const [series, setSeries] = useState([]);

  useEffect(() => {
    let mounted = true;
    (async () => {
      try {
        const resp = await fetch('/api/analytics-visitors');
        const data = await resp.json();
        if (!mounted) return;
        if (resp.ok && data.success) {
          setTotal(data.totalUsers || 0);
          setSeries(data.daily || []);
        } else {
          setError(data.error || 'Failed to fetch');
        }
      } catch (e) {
        if (mounted) setError(e.message || 'Failed to fetch');
      } finally {
        if (mounted) setLoading(false);
      }
    })();
    return () => { mounted = false; };
  }, []);

  const labels = series.map(p => `${p.date?.slice(6,8)}${['jan','feb','mar','apr','may','jun','jul','aug','sep','oct','nov','dec'][Number(p.date?.slice(4,6))-1]}`);
  const values = series.map(p => p.users);

  const data = {
    labels,
    datasets: [{
      label: 'Users',
      data: values,
      borderColor: 'rgb(16,185,129)',
      backgroundColor: 'rgba(16,185,129,0.2)',
      pointBackgroundColor: 'rgb(16,185,129)',
      tension: 0.2,
      pointRadius: 3,
      fill: false,
    }]
  };

  const options = {
    responsive: true,
    plugins: { legend: { display: false } },
    scales: {
      x: { ticks: { color: '#222' }, title: { display: true, text: 'Date' } },
      y: { ticks: { color: '#222' }, title: { display: true, text: 'Users' }, beginAtZero: true },
    }
  };

  return (
    <div className="bg-white rounded-xl shadow p-6 border border-blue-100 flex flex-col items-center justify-center">
      <div className="text-lg font-semibold mb-2 text-blue-700">Visitors (GA4)</div>
      {loading ? (
        <div className="text-blue-600 font-semibold">Loading...</div>
      ) : error ? (
        <div className="text-red-600 text-sm">{error}</div>
      ) : (
        <>
          <div className="text-3xl font-bold text-blue-900">{total.toLocaleString()}</div>
          <div className="w-full mt-4 bg-white rounded-lg p-2">
            <Line data={data} options={options} />
          </div>
          <div className="text-xs mt-2 text-blue-700/70">Last 30 days total; 14-day trend</div>
        </>
      )}
    </div>
  );
}
