+import { useEffect, useState } from "react";
+import { useRouter } from "next/router";
+
+// Mock booking data for demo
+const initialBookings = [
+  { id: 1, name: "John Doe", service: "Complete Interior Detail", date: "2025-10-10", amount: 130, status: "complete", phone: "902-555-1234", email: "john@example.com", location: "Halifax" },
+  { id: 2, name: "Jane Smith", service: "Premium Exterior Wash", date: "2025-10-12", amount: 50, status: "pending", phone: "902-555-5678", email: "jane@example.com", location: "Dartmouth" },
+  { id: 3, name: "Alex Lee", service: "Ultimate Full Detail", date: "2025-10-15", amount: 160, status: "complete", phone: "902-555-9999", email: "alex@example.com", location: "Bedford" },
+];
