+export default function AdminDashboard() {
+  const router = useRouter();
+  const [bookings, setBookings] = useState(initialBookings);
+  const [showAdd, setShowAdd] = useState(false);
+  const [showDetail, setShowDetail] = useState(false);
+  const [detailBooking, setDetailBooking] = useState(null);
+  // Service/add-on logic
+  const serviceOptions = [
+    { title: "Premium Exterior Wash", basePrice: 50, hasAddOns: false },
+    { title: "Complete Interior Detail", basePrice: 110, hasAddOns: true },
+    { title: "Ultimate Full Detail", basePrice: 150, hasAddOns: true },
+  ];
+  const addOnOptions = [
+    { label: "Pet Hair Removal", price: 20 },
+  ];
+  const carTypeOptions = [
+    { label: "Sedan", price: 0 },
+    { label: "SUV", price: 10 },
+    { label: "Truck", price: 20 },
+  ];
+  // Booking state
+  const [newBooking, setNewBooking] = useState({
+    name: "",
+    service: serviceOptions[0].title,
+    date: "",
+    amount: serviceOptions[0].basePrice,
+    status: "pending",
+    phone: "",
+    email: "",
+    location: "",
+    addOns: [],
+    carType: carTypeOptions[0].label,
+  });
+  const [locationSuggestions, setLocationSuggestions] = useState([]);
+
+  useEffect(() => {
+    if (typeof window !== "undefined") {
+      const saved = window.localStorage.getItem("adminAuth");
+      if (saved !== "Detailing1313!") {
+        router.replace("/admin");
+      }
+    }
+  }, [router]);
+
+  // Calculate total revenue
+  const totalRevenue = bookings.reduce((sum, b) => sum + (b.amount || 0), 0);
+
+  // Add new booking
+  // Calculate price
+  const calculatePrice = (serviceTitle, addOns, carTypeLabel) => {
+    const service = serviceOptions.find(s => s.title === serviceTitle);
+    let price = service ? service.basePrice : 0;
+    if (service && service.hasAddOns && addOns.length) {
+      price += addOns.reduce((sum, a) => sum + a.price, 0);
+    }
+    const carType = carTypeOptions.find(c => c.label === carTypeLabel);
+    if (carType) price += carType.price;
+    // Special rule for Complete Interior Detail
+    if (serviceTitle === "Complete Interior Detail") {
+      if (carTypeLabel === "SUV") price += 20;
+      else if (carTypeLabel === "Truck") price += 30;
+    }
+    return price;
+  };
+
+  // Add booking
+  const handleAddBooking = (e) => {
+    e.preventDefault();
+    setBookings([
+      ...bookings,
+      {
+        ...newBooking,
+        id: bookings.length + 1,
+        amount: calculatePrice(newBooking.service, newBooking.addOns, newBooking.carType),
+      }
+    ]);
+    setNewBooking({
+      name: "",
+      service: serviceOptions[0].title,
+      date: "",
+      amount: serviceOptions[0].basePrice,
+      status: "pending",
+      phone: "",
+      email: "",
+      location: "",
+      addOns: [],
+      carType: carTypeOptions[0].label,
+    });
+    setShowAdd(false);
+  };
+
+  // Show booking details modal
+  const handleShowDetail = (booking) => {
+    setDetailBooking(booking);
+    setShowDetail(true);
+  };
+
+  return (
+    <div className="min-h-screen bg-gray-100 py-10 px-4">
+      <div className="max-w-5xl mx-auto">
+        <h1 className="text-3xl font-bold mb-8 text-center" style={{ color: '#000' }}>Admin Booking Dashboard</h1>
+        {/* Revenue summary */}
+        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
+          <div className="bg-gradient-to-br from-blue-600 to-blue-400 text-white rounded-xl shadow p-6 flex flex-col items-center justify-center">
+            <div className="text-lg font-semibold mb-2">Total Revenue</div>
+            <div className="text-3xl font-bold">${totalRevenue}</div>
+          </div>
+          <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center border border-blue-100">
+            <div className="text-lg font-semibold mb-2 text-blue-700">Bookings</div>
+            <div className="text-3xl font-bold text-blue-900">{bookings.length}</div>
+          </div>
+          <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center border border-blue-100">
+            <div className="text-lg font-semibold mb-2 text-blue-700">Pending</div>
+            <div className="text-3xl font-bold text-yellow-600">{bookings.filter(b => b.status === "pending").length}</div>
+          </div>
+        </div>
+        {/* Booking list */}
+        <div className="bg-white rounded-xl shadow p-6 border border-blue-100 mb-8">
+          <div className="flex justify-between items-center mb-4">
+            <h2 className="text-xl font-bold" style={{ color: '#888' }}>Booking History</h2>
+            <button
+              className="py-2 px-4 rounded-lg bg-blue-600 hover:bg-blue-700 text-white font-semibold"
+              onClick={() => setShowAdd(true)}
+            >
+              Add Booking
+            </button>
+          </div>
+          <table className="w-full text-left border-collapse">
+            <thead>
+              <tr style={{ background: '#f5f5f5' }}>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Name</th>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Service</th>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Date</th>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Amount</th>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Status</th>
+              </tr>
+            </thead>
+            <tbody>
+              {bookings.map(b => (
+                <tr key={b.id} style={{ background: '#fff', color: '#222' }}>
+                  <td className="py-2 px-3" style={{ color: '#222', cursor: 'pointer', textDecoration: 'underline' }} onClick={() => handleShowDetail(b)}>{b.name}</td>
+                  <td className="py-2 px-3" style={{ color: '#222' }}>{b.service}</td>
+                  <td className="py-2 px-3" style={{ color: '#222' }}>{b.date}</td>
+                  <td className="py-2 px-3" style={{ color: '#222' }}>${b.amount}</td>
+                  <td className="py-2 px-3">
+                    <span className={`px-3 py-1 rounded-full text-xs font-bold ${b.status === "complete" ? "bg-green-200 text-green-800" : b.status === "pending" ? "bg-yellow-200 text-yellow-800" : "bg-gray-200 text-gray-800"}`}>{b.status}</span>
+                  </td>
+                </tr>
+              ))}
+            </tbody>
+          </table>
+        </div>
+        {/* Booking details modal */}
+        {showDetail && detailBooking && (
+          <div className="fixed inset-0 bg-black/30 flex items-center justify-center z-50">
+            <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md border border-blue-100">
+              <h3 className="text-xl font-bold mb-6 text-center" style={{ color: '#888' }}>Booking Details</h3>
+              <div className="space-y-3 text-gray-800">
+                <div><span className="font-semibold">Name:</span> {detailBooking.name}</div>
+                <div><span className="font-semibold">Service:</span> {detailBooking.service}</div>
+                <div><span className="font-semibold">Date:</span> {detailBooking.date}</div>
+                <div><span className="font-semibold">Amount:</span> ${detailBooking.amount}</div>
+                <div><span className="font-semibold">Status:</span> {detailBooking.status}</div>
+                <div><span className="font-semibold">Phone:</span> {detailBooking.phone}</div>
+                <div><span className="font-semibold">Email:</span> {detailBooking.email}</div>
+                <div><span className="font-semibold">Location:</span> {detailBooking.location}</div>
+              </div>
+              <button className="mt-8 w-full py-3 rounded-lg bg-blue-600 hover:bg-blue-700 text-white font-semibold" onClick={() => setShowDetail(false)}>Close</button>
+            </div>
+          </div>
+        )}
+        {/* Add booking modal */}
+        {showAdd && (
+          <div className="fixed inset-0 bg-black/30 flex items-center justify-center z-50">
+            <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md border border-blue-100">
+              <h3 className="text-xl font-bold mb-6 text-center" style={{ color: '#888' }}>Add Booking</h3>
+              <form onSubmit={handleAddBooking} className="space-y-5">
+                {/* Name */}
+                <input type="text" required placeholder="Name" value={newBooking.name} onChange={e => setNewBooking({ ...newBooking, name: e.target.value })} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none text-gray-800" />
+                {/* Service dropdown */}
+                <select required value={newBooking.service} onChange={e => {
+                  const selectedService = e.target.value;
+                  setNewBooking(nb => ({
+                    ...nb,
+                    service: selectedService,
+                    addOns: [],
+                    amount: calculatePrice(selectedService, [], nb.carType),
+                  }));
+                }} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none text-gray-800">
+                  {serviceOptions.map(s => (
+                    <option key={s.title} value={s.title}>{s.title}</option>
+                  ))}
+                </select>
+                {/* Car type dropdown */}
+                <select required value={newBooking.carType} onChange={e => {
+                  const carType = e.target.value;
+                  setNewBooking(nb => ({
+                    ...nb,
+                    carType,
+                    amount: calculatePrice(nb.service, nb.addOns, carType),
+                  }));
+                }} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none text-gray-800">
+                  {carTypeOptions.map(c => (
+                    <option key={c.label} value={c.label}>{c.label}</option>
+                  ))}
+                </select>
+                {/* Add-ons (only for services with add-ons) */}
+                {serviceOptions.find(s => s.title === newBooking.service)?.hasAddOns && (
+                  <div>
+                    <div className="text-base font-semibold text-gray-700 mb-2 text-left">Add-Ons</div>
+                    <div className="flex flex-wrap gap-3 mb-2">
+                      {addOnOptions.map(opt => (
+                        <button
+                          key={opt.label}
+                          type="button"
+                          onClick={() => {
+                            setNewBooking(nb => {
+                              const exists = nb.addOns.find(a => a.label === opt.label);
+                              const updated = exists
+                                ? nb.addOns.filter(a => a.label !== opt.label)
+                                : [...nb.addOns, opt];
+                              return {
+                                ...nb,
+                                addOns: updated,
+                                amount: calculatePrice(nb.service, updated, nb.carType),
+                              };
+                            });
+                          }}
+                          className={`px-5 py-2 rounded-full border font-medium transition-all ${newBooking.addOns.find(a => a.label === opt.label) ? "bg-blue-600 text-white border-blue-600 shadow" : "bg-white border-gray-300 text-blue-600 hover:bg-blue-50"}`}
+                        >
+                          {opt.label}
+                        </button>
+                      ))}
+                    </div>
+                  </div>
+                )}
+                {/* Date */}
+                <input type="date" required value={newBooking.date} onChange={e => setNewBooking({ ...newBooking, date: e.target.value })} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none text-gray-800" />
+                {/* Amount (auto-calculated, read-only) */}
+                <input type="number" required placeholder="Amount" value={newBooking.amount} readOnly className="w-full px-4 py-3 rounded-lg border border-blue-200 bg-gray-100 text-gray-800" />
+                {/* Phone (numbers only) */}
+                <input type="text" required placeholder="Phone" value={newBooking.phone} onChange={e => {
+                  // Only allow numbers
+                  const val = e.target.value.replace(/[^0-9]/g, "");
+                  setNewBooking({ ...newBooking, phone: val });
+                }} maxLength={10} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none text-gray-800" />
+                {/* Email */}
+                <input type="email" required placeholder="Email" value={newBooking.email} onChange={e => setNewBooking({ ...newBooking, email: e.target.value })} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none text-gray-800" />
+                {/* Location autocomplete (Google Places API) */}
+                <input type="text" required placeholder="Location" value={newBooking.location} onChange={async e => {
+                  const val = e.target.value;
+                  setNewBooking({ ...newBooking, location: val });
+                  // Simple Google Places Autocomplete (client-side only)
+                  if (window && val.length > 2) {
+                    try {
+                      const resp = await fetch(`https://maps.googleapis.com/maps/api/place/autocomplete/json?input=${encodeURIComponent(val)}&key=YOUR_GOOGLE_API_KEY&types=(cities)`);
+                      const data = await resp.json();
+                      setLocationSuggestions(data.predictions?.map(p => p.description) || []);
+                    } catch {}
+                  } else {
+                    setLocationSuggestions([]);
+                  }
+                }} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none text-gray-800" autoComplete="off" />
+                {/* Location suggestions dropdown */}
+                {locationSuggestions.length > 0 && (
+                  <div className="bg-white border border-blue-200 rounded-lg shadow mt-1">
+                    {locationSuggestions.map((sug, i) => (
+                      <div key={i} className="px-4 py-2 cursor-pointer hover:bg-blue-50" onClick={() => {
+                        setNewBooking(nb => ({ ...nb, location: sug }));
+                        setLocationSuggestions([]);
+                      }}>{sug}</div>
+                    ))}
+                  </div>
+                )}
+                {/* Status dropdown */}
+                <select value={newBooking.status} onChange={e => setNewBooking({ ...newBooking, status: e.target.value })} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none text-gray-800">
+                  <option value="pending">Pending</option>
+                  <option value="complete">Complete</option>
+                </select>
+                <div className="flex gap-4 mt-4">
+                  <button type="submit" className="w-full py-3 rounded-lg bg-blue-600 hover:bg-blue-700 text-white font-semibold">Add</button>
+                  <button type="button" className="w-full py-3 rounded-lg bg-gray-200 hover:bg-gray-300 text-blue-700 font-semibold" onClick={() => setShowAdd(false)}>Cancel</button>
+                </div>
+              </form>
+            </div>
+          </div>
+        )}
+      </div>
+    </div>
+  );
+}

