+        amount: calculatePrice(newBooking.service, newBooking.addOns, newBooking.carType),
+      }
+    ]);
+    setNewBooking({
+      name: "",
+      service: serviceOptions[0].title,
+      date: "",
+      amount: serviceOptions[0].basePrice,
+      status: "pending",
+      phone: "",
+      email: "",
+      location: "",
+      addOns: [],
+      carType: carTypeOptions[0].label,
+    });
+    setShowAdd(false);
+  };
+
+  // Show booking details modal
+  const handleShowDetail = (booking) => {
+    setDetailBooking(booking);
+    setShowDetail(true);
+  };
+
+  return (
+    <div className="min-h-screen bg-gray-100 py-10 px-4">
