+            <div className="text-lg font-semibold mb-2">Total Revenue</div>
+            <div className="text-3xl font-bold">${totalRevenue}</div>
+          </div>
+          <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center border border-blue-100">
+            <div className="text-lg font-semibold mb-2 text-blue-700">Bookings</div>
+            <div className="text-3xl font-bold text-blue-900">{bookings.length}</div>
+          </div>
+          <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center border border-blue-100">
+            <div className="text-lg font-semibold mb-2 text-blue-700">Pending</div>
+            <div className="text-3xl font-bold text-yellow-600">{bookings.filter(b => b.status === "pending").length}</div>
+          </div>
+        </div>
+        {/* Booking list */}
+        <div className="bg-white rounded-xl shadow p-6 border border-blue-100 mb-8">
+          <div className="flex justify-between items-center mb-4">
+            <h2 className="text-xl font-bold" style={{ color: '#888' }}>Booking History</h2>
+            <button
+              className="py-2 px-4 rounded-lg bg-blue-600 hover:bg-blue-700 text-white font-semibold"
+              onClick={() => setShowAdd(true)}
+            >
+              Add Booking
+            </button>
+          </div>
+          <table className="w-full text-left border-collapse">
+            <thead>
+              <tr style={{ background: '#f5f5f5' }}>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Name</th>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Service</th>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Date</th>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Amount</th>
+                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Status</th>
+              </tr>
+            </thead>
+            <tbody>
+              {bookings.map(b => (
+                <tr key={b.id} style={{ background: '#fff', color: '#222' }}>
+                  <td className="py-2 px-3" style={{ color: '#222', cursor: 'pointer', textDecoration: 'underline' }} onClick={() => handleShowDetail(b)}>{b.name}</td>
+                  <td className="py-2 px-3" style={{ color: '#222' }}>{b.service}</td>
+                  <td className="py-2 px-3" style={{ color: '#222' }}>{b.date}</td>
+                  <td className="py-2 px-3" style={{ color: '#222' }}>${b.amount}</td>
