+                </div>
+              </div>
+              <EyeToggle
+                open={showCalendar}
+                onToggle={() => setShowCalendar(prev => !prev)}
+                label="bookings calendar"
+              />
+            </div>
+            <div
+              style={{
+                width: '100%',
+                overflow: 'hidden',
+                transition: 'max-height 0.35s ease, margin-top 0.2s ease',
+                maxHeight: showCalendar ? '420px' : '0px',
+                marginTop: showCalendar ? '16px' : '0px',
