+              <div className="w-full bg-white rounded-lg p-2">
+                <RevenueChart
+                  bookings={bookings}
+                  status="pending"
+                  datasetLabel="Pending Estimate"
+                  borderColor="rgb(202, 138, 4)"
+                  backgroundColor="rgba(202,138,4,0.2)"
+                  pointBackgroundColor="rgb(202, 138, 4)"
