+                <br />
+                <span className="font-semibold">{formatDateShort(expenseToDelete.date)}</span>
+                {" • "}
+                <span className="font-semibold">{expenseToDelete.productName || 'Item'}</span>
+                {" • $"}
+                <span className="font-semibold">{expenseToDelete.amount}</span>
+              </p>
