+                              <span className="hidden sm:inline">{b.status}</span>
+                            </span>
+                          </td>
+                          <td
+                            className="border border-gray-200 py-2 px-2 max-w-[140px] truncate"
+                            style={{ color: '#222', cursor: 'pointer', textDecoration: 'underline' }}
+                            onClick={() => handleEditBooking(b)}
+                          >
