+                          <td className="border border-gray-200 py-2 px-2">
+                            <button
+                              type="button"
+                              className="px-3 py-1 text-xs rounded-full border border-blue-200 text-blue-700 hover:bg-blue-50"
+                              onClick={(event) => {
