import { useEffect, useState } from "react";
import { useRouter } from "next/router";

// Mock booking data for demo
const initialBookings = [
  { id: 1, name: "John Doe", service: "Complete Interior Detail", date: "2025-10-10", amount: 130, status: "complete" },
  { id: 2, name: "Jane Smith", service: "Premium Exterior Wash", date: "2025-10-12", amount: 50, status: "pending" },
  { id: 3, name: "Alex Lee", service: "Ultimate Full Detail", date: "2025-10-15", amount: 160, status: "complete" },
];

export default function AdminDashboard() {
  const router = useRouter();
  const [bookings, setBookings] = useState(initialBookings);
  const [showAdd, setShowAdd] = useState(false);
  const [newBooking, setNewBooking] = useState({ name: "", service: "", date: "", amount: "", status: "pending" });

  useEffect(() => {
    if (typeof window !== "undefined") {
      const saved = window.localStorage.getItem("adminAuth");
      if (saved !== "Detailing1313!") {
        router.replace("/admin");
      }
    }
  }, [router]);

  // Calculate total revenue
  const totalRevenue = bookings.reduce((sum, b) => sum + (b.amount || 0), 0);

  // Add new booking
  const handleAddBooking = (e) => {
    e.preventDefault();
    setBookings([
      ...bookings,
      { ...newBooking, id: bookings.length + 1, amount: Number(newBooking.amount) }
    ]);
    setNewBooking({ name: "", service: "", date: "", amount: "", status: "pending" });
    setShowAdd(false);
  };

  return (
    <div className="min-h-screen bg-gray-100 py-10 px-4">
      <div className="max-w-5xl mx-auto">
        <h1 className="text-3xl font-bold mb-8 text-center" style={{ color: '#000' }}>Admin Booking Dashboard</h1>
        {/* Revenue summary */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-10">
          <div className="bg-gradient-to-br from-blue-600 to-blue-400 text-white rounded-xl shadow p-6 flex flex-col items-center justify-center">
            <div className="text-lg font-semibold mb-2">Total Revenue</div>
            <div className="text-3xl font-bold">${totalRevenue}</div>
          </div>
          <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center border border-blue-100">
            <div className="text-lg font-semibold mb-2 text-blue-700">Bookings</div>
            <div className="text-3xl font-bold text-blue-900">{bookings.length}</div>
          </div>
          <div className="bg-white rounded-xl shadow p-6 flex flex-col items-center justify-center border border-blue-100">
            <div className="text-lg font-semibold mb-2 text-blue-700">Pending</div>
            <div className="text-3xl font-bold text-yellow-600">{bookings.filter(b => b.status === "pending").length}</div>
          </div>
        </div>
        {/* Booking list */}
        <div className="bg-white rounded-xl shadow p-6 border border-blue-100 mb-8">
          <div className="flex justify-between items-center mb-4">
            <h2 className="text-xl font-bold" style={{ color: '#888' }}>Booking History</h2>
            <button
              className="py-2 px-4 rounded-lg bg-blue-600 hover:bg-blue-700 text-white font-semibold"
              onClick={() => setShowAdd(true)}
            >
              Add Booking
            </button>
          </div>
          <table className="w-full text-left border-collapse">
            <thead>
              <tr style={{ background: '#f5f5f5' }}>
                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Name</th>
                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Service</th>
                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Date</th>
                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Amount</th>
                <th className="py-2 px-3 font-semibold" style={{ color: '#000' }}>Status</th>
              </tr>
            </thead>
            <tbody>
              {bookings.map(b => (
                <tr key={b.id} style={{ background: '#fff', color: '#222' }}>
                  <td className="py-2 px-3" style={{ color: '#222' }}>{b.name}</td>
                  <td className="py-2 px-3" style={{ color: '#222' }}>{b.service}</td>
                  <td className="py-2 px-3" style={{ color: '#222' }}>{b.date}</td>
                  <td className="py-2 px-3" style={{ color: '#222' }}>${b.amount}</td>
                  <td className="py-2 px-3">
                    <span className={`px-3 py-1 rounded-full text-xs font-bold ${b.status === "complete" ? "bg-green-200 text-green-800" : b.status === "pending" ? "bg-yellow-200 text-yellow-800" : "bg-gray-200 text-gray-800"}`}>{b.status}</span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
        {/* Add booking modal */}
        {showAdd && (
          <div className="fixed inset-0 bg-black/30 flex items-center justify-center z-50">
            <div className="bg-white rounded-2xl shadow-xl p-8 w-full max-w-md border border-blue-100">
              <h3 className="text-xl font-bold mb-6 text-center" style={{ color: '#888' }}>Add Booking</h3>
              <form onSubmit={handleAddBooking} className="space-y-5">
                <input type="text" required placeholder="Name" value={newBooking.name} onChange={e => setNewBooking({ ...newBooking, name: e.target.value })} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none" />
                <input type="text" required placeholder="Service" value={newBooking.service} onChange={e => setNewBooking({ ...newBooking, service: e.target.value })} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none" />
                <input type="date" required value={newBooking.date} onChange={e => setNewBooking({ ...newBooking, date: e.target.value })} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none" />
                <input type="number" required placeholder="Amount" value={newBooking.amount} onChange={e => setNewBooking({ ...newBooking, amount: e.target.value })} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none" />
                <select value={newBooking.status} onChange={e => setNewBooking({ ...newBooking, status: e.target.value })} className="w-full px-4 py-3 rounded-lg border border-blue-200 focus:border-blue-500 focus:ring-2 focus:ring-blue-500 outline-none">
                  <option value="pending">Pending</option>
                  <option value="complete">Complete</option>
                </select>
                <div className="flex gap-4 mt-4">
                  <button type="submit" className="w-full py-3 rounded-lg bg-blue-600 hover:bg-blue-700 text-white font-semibold">Add</button>
                  <button type="button" className="w-full py-3 rounded-lg bg-gray-200 hover:bg-gray-300 text-blue-700 font-semibold" onClick={() => setShowAdd(false)}>Cancel</button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
