import { useState } from "react";

const shineOptions = [
  { label: "Wax", price: 0 },
  { label: "Ceramic Sealant", price: 10 },
  { label: "Ceramic Coating", price: 20 },
];

const deconOptions = [
  { label: "Clay Bar", price: 10 },
  { label: "Iron Remover", price: 20 },
];

export default function Services() {
  const [shine, setShine] = useState(shineOptions[0]);
  const [decon, setDecon] = useState([]);

  const basePrice = 60;
  const total =
    basePrice +
    shine.price +
    decon.reduce((sum, opt) => sum + opt.price, 0);

  const toggleDecon = (option) => {
    setDecon((prev) =>
      prev.some((o) => o.label === option.label)
        ? prev.filter((o) => o.label !== option.label)
        : [...prev, option]
    );
  };

  return (
    <section className="min-h-screen bg-[var(--color-bg-dark)] py-12 px-4 flex flex-col items-center">
      <div className="w-full max-w-2xl bg-[var(--color-surface)] rounded-2xl shadow-2xl border border-[var(--color-border)] p-8">
        <h1 className="text-3xl md:text-4xl font-bold mb-2 text-white font-heading">Exterior Detailing</h1>
        <div className="flex items-baseline gap-2 mb-6">
          <span className="text-2xl font-semibold text-white">${basePrice}</span>
          <span className="text-[var(--color-text-muted)] text-base">Base Price</span>
        </div>

        {/* Shine & Coating */}
        <div className="mb-6">
          <h2 className="text-lg font-semibold text-white mb-2">Shine &amp; Coating</h2>
          <div className="flex flex-wrap gap-3">
            {shineOptions.map((opt) => (
              <button
                key={opt.label}
                onClick={() => setShine(opt)}
                className={`px-5 py-2 rounded-full border transition-all font-medium
                  ${
                    shine.label === opt.label
                      ? "bg-[var(--color-primary)] text-white shadow"
                      : "bg-transparent border-[var(--color-border)] text-[var(--color-text-secondary)] hover:bg-[var(--color-primary)]/10"
                  }
                `}
              >
                {opt.label}
                {opt.price > 0 && (
                  <span className="ml-2 text-[var(--color-accent)] font-semibold">+${opt.price}</span>
                )}
              </button>
            ))}
          </div>
        </div>

        {/* Decontamination */}
        <div className="mb-8">
          <h2 className="text-lg font-semibold text-white mb-2">Decontamination</h2>
          <div className="flex flex-wrap gap-3">
            {deconOptions.map((opt) => {
              const selected = decon.some((o) => o.label === opt.label);
              return (
                <button
                  key={opt.label}
                  onClick={() => toggleDecon(opt)}
                  className={`px-5 py-2 rounded-full border transition-all font-medium
                    ${
                      selected
                        ? "bg-[var(--color-primary)] text-white shadow"
                        : "bg-transparent border-[var(--color-border)] text-[var(--color-text-secondary)] hover:bg-[var(--color-primary)]/10"
                    }
                  `}
                >
                  {opt.label}
                  <span className="ml-2 text-[var(--color-accent)] font-semibold">+${opt.price}</span>
                </button>
              );
            })}
          </div>
        </div>

        {/* Total */}
        <div className="flex items-center justify-between mt-8 border-t border-[var(--color-border)] pt-6">
          <span className="text-xl font-semibold text-white">Total</span>
          <span className="text-2xl font-bold text-[var(--color-primary)]">${total}</span>
        </div>
      </div>
    </section>
  );
}
