+@import "tailwindcss";
+html, body { background-color: transparent !important; }
+
+
+/* ================================
+   THEME VARIABLES
+================================ */
+:root {
+    /* Brand */
+    --color-primary: #0076ff; /* WL Blue */
+    --color-accent: #33cfff; /* Light accent blue */
+
+    /* Surfaces */
+    --color-bg-dark: #0a0a0a;
+    --color-bg-light: #ffffff;
+    --color-surface: #111214; /* cards / sections */
+    --color-border: #1f1f1f;
+
+    /* Text */
+    --color-text-main: #f9f9f9;
+    --color-text-secondary: #d6d6d6;
+
+    --color-text-muted: #8a8a8a;
+
+    /* Gradients (opt-in) */
+    --gradient-primary: linear-gradient(90deg, #0076ff 0%, #33cfff 100%);
+
+    /* Typography */
+    --font-heading: "Poppins", sans-serif;
+    --font-body: "Inter", sans-serif;
+
+    /* Radius & shadow */
+    --radius-sm: 8px;
+    --radius-md: 12px;
+    --radius-lg: 16px;
+    --shadow-lg: 0 10px 30px rgba(0, 0, 0, 0.35);
+}
+
+/* Optional: light scheme support if you ever switch */
+@media (prefers-color-scheme: light) {
+    :root {
+        --color-bg-light: #ffffff;
+        --color-text-main: #171717;
+        --color-text-secondary: #4a4a4a;
+        --color-text-muted: #6b6b6b;
+    }
+}
+
+/* ================================
+   BASE
+================================ */
+*,
+*::before,
+*::after {
+    box-sizing: border-box;
+}
+
+html,
+body {
+    height: 100%;
+}
+
+body {
+    background-color: var(--color-bg-dark);
+    color: var(--color-text-main);
+    font-family: var(--font-body);
+    -webkit-font-smoothing: antialiased;
+    -moz-osx-font-smoothing: grayscale;
+    transition: background-color 0.3s ease, color 0.3s ease;
+}
+
+/* Transparent images stay transparent */
+img {
+    background-color: transparent !important;
+    mix-blend-mode: normal;
+}
+
+/* Typography */
+h1,
+h2,
+h3,
+h4,
+h5,
+h6 {
+    font-family: var(--font-heading);
+    color: var(--color-text-main);
+    line-height: 1.2;
+}
+
+p {
+    color: var(--color-text-secondary);
+}
+
+a {
+    color: var(--color-primary);
+    text-decoration: none;
+    transition: color 0.2s ease;
+}
+a:hover {
+    color: var(--color-accent);
+}
+
+/* Utility helpers */
+.no-bg {
+    background: transparent !important;
+}
+.text-muted {
+    color: var(--color-text-muted);
+}
+.border-default {
+    border: 1px solid var(--color-border);
+}
+
+/* ================================
+   COMPONENT PRIMITIVES (opt-in)
+   Use these classes explicitly.
+================================ */
+
+/* Buttons */
+.btn {
+    display: inline-flex;
+    align-items: center;
+    justify-content: center;
+    gap: 0.5rem;
+    padding: 0.6rem 1.2rem;
+    border: none;
+    border-radius: var(--radius-md);
+    font-family: var(--font-heading);
+    font-weight: 600;
+    cursor: pointer;
+    transition: transform 0.2s ease, box-shadow 0.2s ease, background 0.2s ease,
+        color 0.2s ease;
+}
+
+.btn-primary {
+    background: var(--gradient-primary);
+    color: #ffffff;
+}
+.btn-primary:hover {
+    transform: translateY(-1px);
+    box-shadow: 0 0 12px rgba(51, 207, 255, 0.5);
+}
+
+.btn-outline {
+    background: transparent;
+    color: var(--color-primary);
+    border: 1px solid var(--color-primary);
+}
+.btn-outline:hover {
+    background: rgba(0, 118, 255, 0.08);
+}
+
+.btn-ghost {
+    background: transparent;
+    color: var(--color-text-secondary);
+}
+.btn-ghost:hover {
+    color: var(--color-primary);
+}
+
+/* Cards / surfaces */
+.card {
+    background: var(--color-surface);
+    border: 1px solid var(--color-border);
+    border-radius: var(--radius-lg);
+    box-shadow: var(--shadow-lg);
+}
+
+/* Nav links (optional helper if you want consistent nav styling) */
+.nav-link {
+    color: var(--color-text-secondary);
+    font-weight: 600;
+    transition: color 0.2s ease, opacity 0.2s ease;
+}
+.nav-link:hover {
+    color: var(--color-primary);
+}
+
+/* Inputs (optional baseline) */
+.input {
+    background: #0f0f10;
+    color: var(--color-text-main);
+    border: 1px solid var(--color-border);
+    border-radius: var(--radius-sm);
+    padding: 0.6rem 0.8rem;
+    outline: none;
+    transition: border-color 0.2s ease, box-shadow 0.2s ease;
+}
+.input:focus {
+    border-color: var(--color-primary);
+    box-shadow: 0 0 0 3px rgba(0, 118, 255, 0.25);
+}
+
+/* Badges / chips */
+.chip {
+    display: inline-flex;
+    align-items: center;
+    gap: 0.4rem;
+    padding: 0.25rem 0.6rem;
+    border-radius: 999px;
+    background: #0f1720;
+    color: var(--color-accent);
+    border: 1px solid rgba(51, 207, 255, 0.25);
+}
+
+/* ================================
+   ACCESSIBILITY
+================================ */
+.sr-only {
+    position: absolute !important;
+    width: 1px !important;
+    height: 1px !important;
+    padding: 0 !important;
+    margin: -1px !important;
+    overflow: hidden !important;
+    clip: rect(0, 0, 0, 0) !important;
+    white-space: nowrap !important;
+    border: 0 !important;
+}
+
+/* Calendar status dots */
+.cal-dot {
+    width: 8px;
+    height: 8px;
+    border-radius: 9999px;
+    display: inline-block;
+}
+.cal-dot-green { background-color: #16a34a; }
+.cal-dot-yellow { background-color: #ca8a04; }
+
+.status-calendar .react-calendar {
+    width: 100%;
+    border: 1px solid #374151; /* gray-700 */
+    border-radius: 12px;
+    padding: 4px;
+    background: #111214; /* dark surface */
+    color: #e5e7eb; /* gray-200 text */
+}
+
+/* Calendar navigation (month label + arrows) */
+.status-calendar .react-calendar__navigation {
+    display: flex;
+    align-items: center;
+    margin-bottom: 6px;
+}
+.status-calendar .react-calendar__navigation button {
+    color: #e5e7eb;
+    background: transparent;
+    border-radius: 8px;
+    min-height: 32px;
+}
+.status-calendar .react-calendar__navigation button:enabled:hover,
+.status-calendar .react-calendar__navigation button:enabled:focus {
+    background: #1f2937; /* gray-800 */
+}
+.status-calendar .react-calendar__navigation__label {
+    font-weight: 700;
+}
+
+/* Weekday headers */
+.status-calendar .react-calendar__month-view__weekdays {
+    text-align: center;
+}
+.status-calendar .react-calendar__month-view__weekdays__weekday abbr {
+    text-decoration: none;
+    color: #9ca3af; /* gray-400 */
+    font-weight: 600;
+}
+
+/* Day tiles */
+.status-calendar .react-calendar__tile {
+    color: #e5e7eb; /* gray-200 */
+    border-radius: 8px;
+}
+.status-calendar .react-calendar__tile:enabled:hover,
+.status-calendar .react-calendar__tile:enabled:focus {
+    background: #1f2937; /* gray-800 */
+}
+.status-calendar .react-calendar__tile--now {
+    background: #0b1220; /* subtle blue-ish dark */
+    color: #ffffff;
+}
+.status-calendar .react-calendar__tile--active {
+    background: #1d4ed8; /* blue-700 */
+    color: #ffffff;
+}
+
+/* Neighboring month days (previous/next displayed within current month) */
+.status-calendar .react-calendar__month-view__days__day--neighboringMonth {
+    color: #6b7280; /* gray-500 */
+    opacity: 0.6;
+}
\ No newline at end of file

commits.content:
@import "tailwindcss";
@tailwind base;
@tailwind components;
@tailwind utilities;


:root {
  --background: #ffffff;
  --foreground: #171717;
}

@theme inline {
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --font-sans: var(--font-geist-sans);
  --font-mono: var(--font-geist-mono);
}

@media (prefers-color-scheme: dark) {
  :root {
    --background: #0a0a0a;
    --foreground: #ededed;
  }
}

body {
  background: var(--background);
  color: var(--foreground);
  font-family: Arial, Helvetica, sans-serif;
}
